import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { Link } from 'react-router-dom';

class BookItem extends React.Component{

  constructor(){
    super();

    this.DeleteBook = this.DeleteBook.bind(this);
  }

  // Delete a book
  DeleteBook(e){
    console.log("Delete Clicked");
    axios.delete("http://localhost:4000/api/books/"+this.props.book._id)
    .then(()=>{
      this.props.ReloadDataMethod();
    })
    .catch();
  }

    render(){
      return(
        <div>
          
          <header className="bg-danger text-center py-5 mb-4">
            <div className="container">
              <h1 className="font-weight-light text-white">Your Books</h1>
            </div>
          </header>
           
          <div class="col-lg-4">
            <div class="card mb-5 mb-lg-0">
              <div class="card-body">
                <h5 class="card-title text-muted text-uppercase text-center">Free</h5>
                  <h6 class="card-price text-center">$0<span class="period">/month</span></h6>
                  <ul class="fa-ul">
                    <li><span class="fa-li"><i class="fas fa-check"></i></span>Single User</li>
                    <li><span class="fa-li"><i class="fas fa-check"></i></span>5GB Storage</li>
                    <li><span class="fa-li"><i class="fas fa-check"></i></span>Unlimited Public Projects</li>
                    <li><span class="fa-li"><i class="fas fa-check"></i></span>Community Access</li>
                    <li class="text-muted"><span class="fa-li"><i class="fas fa-times"></i></span>Unlimited Private Projects</li>
                    <li class="text-muted"><span class="fa-li"><i class="fas fa-times"></i></span>Dedicated Phone Support</li>
                    <li class="text-muted"><span class="fa-li"><i class="fas fa-times"></i></span>Free Subdomain</li>
                    <li class="text-muted"><span class="fa-li"><i class="fas fa-times"></i></span>Monthly Status Reports</li>
                  </ul>
                  <a href="#" class="btn btn-block btn-primary text-uppercase">Button</a>
              </div>
            </div>
            </div>

            <div class="col-lg-4">
            <div class="card mb-5 mb-lg-0">
              <div class="card-body">
                <h5 class="card-title text-muted text-uppercase text-center">Free</h5>
                  <h6 class="card-price text-center">$0<span class="period">/month</span></h6>
                  <ul class="fa-ul">
                    <li><span class="fa-li"><i class="fas fa-check"></i></span>Single User</li>
                    <li><span class="fa-li"><i class="fas fa-check"></i></span>5GB Storage</li>
                    <li><span class="fa-li"><i class="fas fa-check"></i></span>Unlimited Public Projects</li>
                    <li><span class="fa-li"><i class="fas fa-check"></i></span>Community Access</li>
                    <li class="text-muted"><span class="fa-li"><i class="fas fa-times"></i></span>Unlimited Private Projects</li>
                    <li class="text-muted"><span class="fa-li"><i class="fas fa-times"></i></span>Dedicated Phone Support</li>
                    <li class="text-muted"><span class="fa-li"><i class="fas fa-times"></i></span>Free Subdomain</li>
                    <li class="text-muted"><span class="fa-li"><i class="fas fa-times"></i></span>Monthly Status Reports</li>
                  </ul>
                  <a href="#" class="btn btn-block btn-primary text-uppercase">Button</a>
              </div>
            </div>
            </div>
 
            
            {/*
            <Card  border="primary" style={{ width: '28rem' }}>
              <Card.Header>{this.props.book.title}</Card.Header>
              <Card.Body>
                <blockquote className="blockquote mb-0">
                <img src={this.props.book.cover}></img>
                  <footer>
                  {this.props.book.year}
                  </footer>
                </blockquote>
              </Card.Body>
              <Button variant="danger" onClick={this.DeleteBook}>Delete</Button>
              <Link to={"/editbook/"+this.props.book._id} className="btn btn-primary">Edit</Link>
            </Card>*/}
        </div>
      )
    }
}
export default BookItem;
