import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Badge from 'react-bootstrap/Badge';
import axios from 'axios';
import { Link } from 'react-router-dom';

class BookItem extends React.Component{

  constructor(){
    super();

    this.DeleteBook = this.DeleteBook.bind(this);
  }

  // Delete a book
  DeleteBook(e){
    console.log("Delete Clicked");
    axios.delete("http://localhost:4000/api/books/"+this.props.book._id)
    .then(()=>{
      this.props.ReloadDataMethod();
    })
    .catch();
  }

    render(){
      return(
        <div> 
          <Card bg="light" border="primary" style={{ width: '25rem', margin: '10px 20px' }}>
          <p style={{color: this.props.book.status === 0 ? "red" : "green"}}>{this.props.book.status}%</p>
          <Badge variant="success">{this.props.book.status}</Badge>
            <Card.Header><h4>{this.props.book.title}</h4> 
            {this.props.book.author}
            </Card.Header>
            <Card.Img variant="top" src={this.props.book.cover} alt="Book Cover Unavailable"></Card.Img>
            
            <Card.Body>
              <blockquote className="blockquote mb-0">
              <footer>
                {this.props.book.year}
              </footer>
              </blockquote>
            </Card.Body>
            <Link to={"/editbook/"+this.props.book._id} className="btn btn-primary">Edit</Link>
            <Button variant="danger" onClick={this.DeleteBook}>Delete</Button>
          </Card>
        </div>
      )
    }
}
export default BookItem;
