import React from 'react';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Badge from 'react-bootstrap/Badge';
import axios from 'axios';
import { Link } from 'react-router-dom';

class BookItem extends React.Component{

  constructor(){
    super();

    this.DeleteBook = this.DeleteBook.bind(this);
  }

  // Delete a book
  DeleteBook(e){
    console.log("Delete Clicked");
    axios.delete("http://localhost:4000/api/books/"+this.props.book._id)
    .then(()=>{
      this.props.ReloadDataMethod();
    })
    .catch();
  }

    render(){
      return(
        <div>
          
          <header className="bg-danger text-center py-5 mb-4">
            <div className="container">
              <h1 className="font-weight-light text-white">Your Books</h1>
            </div>
          </header>
           
          <div class="col-lg-4">
            <div class="card mb-5 mb-lg-0">
              <div class="card-body">
                <h5 class="card-title text-muted text-uppercase text-center">{this.props.book.title}</h5>
                <h6 class="text-center badge">{this.props.book.status}</h6>
                  <blockquote className="blockquote mb-0">
                <img src={this.props.book.cover}></img>
                  <footer>
                  {this.props.book.year}
                  </footer>
                </blockquote>
                <Badge variant="info">test</Badge>
                  <a href="#" class="btn btn-block btn-primary text-uppercase">Button</a>
              </div>
            </div>
            </div>
          
 
            
            {/*
            <Card  border="primary" style={{ width: '28rem' }}>
